mapper.select:
select
distinct
from e_activity a join b_user u on u.user_id = a.user_id_enterprise
and u.user_id = #{userId}
order by (a.END_DATE_TIME - now()) desc
limit #{start}, #{limit}

    select count(1) from e_activity a
	join b_user u on u.user_id = a.user_id_enterprise




				and u.user_id = #{userId}

select
distinct
, u.user_id, u.username, u.nickname, uEnt.username as username_enterprise, uEnt.user_id as user_id_enterprise from e_activity a, rel_activity_user rau, b_user u, b_user uEnt
and a.activity_id = rau.activity_id
and rau.user_id = #{userId}
and a.type_code = #{typeCode}
and rau.user_id = u.user_id and a.user_Id_Enterprise = uEnt.user_id and a.END_DATE_TIME - now() >= 0
order by (a.END_DATE_TIME - now()) desc
limit #{start}, #{limit}

select count(1) from e_activity a, rel_activity_user rau, b_user u, b_user uEnt
and a.activity_id = rau.activity_id
and rau.user_id = #{userId}
and a.type_code = #{typeCode}
and rau.user_id = u.user_id and a.user_Id_Enterprise = uEnt.user_id and a.END_DATE_TIME - now() >= 0

select
, u.user_id, u.username, u.nickname, uEnt.username as username_enterprise, uEnt.user_id as user_id_enterprise from e_activity a, rel_activity_user rau, b_user u, b_user uEnt
and a.activity_id = rau.activity_id and rau.user_id = u.user_id and a.user_Id_Enterprise = uEnt.user_id and a.ACTIVITY_ID = #{activityId}

select
, u.user_id, u.username, u.nickname, uEnt.username as username_enterprise, uEnt.user_id as user_id_enterprise , b.WX_AUTHORIZER_INFO_ID B_WX_AUTHORIZER_INFO_ID, b.USER_ID B_USER_ID, b.NICK_NAME B_NICK_NAME, b.HEAD_IMG B_HEAD_IMG , b.SERVICE_TYPE_INFO B_SERVICE_TYPE_INFO, b.VERIFY_TYPE_INFO B_VERIFY_TYPE_INFO, b.USER_NAME B_USER_NAME , b.ALIAS B_ALIAS, b.QRCODE_URL B_QRCODE_URL, b.BUSINESS_INFO B_BUSINESS_INFO , b.IDC B_IDC, b.AUTHORIZER_APPID B_AUTHORIZER_APPID, b.FUNC_INFO B_FUNC_INFO , b.QRCODE_FILE_PATH B_QRCODE_FILE_PATH , b.CREATE_DATE_TIME B_CREATE_DATE_TIME from e_activity a, rel_activity_user rau, b_user u, b_user uEnt, wx_authorizer_info b
and a.activity_id = rau.activity_id and rau.user_id = u.user_id and a.user_Id_Enterprise = uEnt.user_id and u.user_id = b.user_id and a.ACTIVITY_ID = #{activityId}

    select

      distinct


			, u.user_id, u.username, u.nickname
    from e_activity a, rel_activity_apply2 b, p_apply2 c, b_user u, b_user uMedia, rel_activity_user d, rel_wx_index_map e



			and a.activity_id = b.activity_id
			and b.apply2_id = c.apply2_id
			and c.union_id = u.username
			and uMedia.user_id = d.user_id
			and d.activity_id = a.activity_id
			and d.user_id = e.user_id


				and c.open_id = #{openId}


				and u.username = #{unionId}


				and e.WX_INDEX_CODE = #{wxIndexCode}


				and a.TYPE_CODE = #{typeCode}




  	order by a.END_DATE_TIME desc

		limit #{start}, #{limit}

    select count(1) 
    from e_activity a, rel_activity_apply2 b, p_apply2 c, b_user u, b_user uMedia, rel_activity_user d, wx_authorizer_info e



			and a.activity_id = b.activity_id
			and b.apply2_id = c.apply2_id
			and c.union_id = u.username
			and uMedia.user_id = d.user_id
			and d.activity_id = a.activity_id
			and d.user_id = e.user_id


				and c.open_id = #{openId}


				and u.username = #{unionId}


				and e.WX_INDEX_CODE = #{wxIndexCode}


				and a.TYPE_CODE = #{typeCode}
